mapper.select:
SELECT MEMBER_ID, ALERT_TYPE, ALERT_TIME, LAT, LON, VIEW_OR_NOT
FROM ALERTS
WHERE MEMBER_ID = #{id}

   	SELECT SUBSCRIPTION_JSON
		FROM MEMBERS
  		WHERE FAMILY_CD = (
       SELECT FAMILY_CD FROM MEMBERS WHERE MEMBER_ID = #{childId}
   	)
   	AND MEM_TYPE = 'P'

mapper.update:
UPDATE ALERTS
SET VIEW_OR_NOT = 1
WHERE MEMBER_ID = #{id}
